import React from 'react';
import classes from './UserBlock.module.css';
import {Row, Col} from 'react-bootstrap';

const userBlock = props => (
    <div className = {classes.UserBlock}>
        <Row>
                    
                    {!this.props.preview ? <Col md={6}> <img src={this.props.source} alt="truck" /> </Col> : null}
                    
                    <Col md={6}>
                        <h1>
                            {this.props.brand} {this.props.model}
                        </h1>
                        <ul>
                            <li>
                                Price:{" "}
                                <strong>
                                    {this.formatPriceString(this.state.price)}
                                </strong>
                                <select
                                    id="currency"
                                    onChange={this.changeCurrency}
                                >
                                    <option value="USD">$</option>
                                    <option value="EUR">€</option>
                                    <option value="UAH">₴</option>
                                </select>
                            </li>
                            <li>
                                License plate:{" "}
                                <strong>{this.props.licensePlate}</strong>
                            </li>
                            <li>
                                Country: <strong>{this.props.country}</strong>
                            </li>
                            <li>
                                Year: <strong>{this.props.yearGraduation} </strong>
                                {this.props.years !== 0 ? this.props.years === 1 ? <span>({this.props.years} year)</span> :<span>({this.props.years} years)</span> : <span>(new)</span>}
                            </li>
                        </ul>
                        {this.props.admin ? (
                            <div
                                style={{
                                    position: "absolute",
                                    right: "15px",
                                    bottom: "0",
                                }}
                            >
                                <button
                                    onClick={this.enableEditMode}
                                    style={{
                                        width: "50px",
                                        borderRadius: "10px",
                                    }}
                                >
                                    Edit
                                </button>
                            </div>
                        ) : null}
                    </Col>
                </Row>
        <h2>Information about you:</h2>
        <img src="https://www.pngitem.com/pimgs/m/247-2472278_admin-admin-icon-png-transparent-png.png" alt="user" width="200px" height = "200px"/>
        <p>{props.name}</p>
        <p>{props.age}</p>
        <p>{props.status}</p>
        <p>{props.dateOfBirth}</p>
        <p>{props.email}</p>
        <p>{props.sex}</p>
        <p>{props.imagePath}</p>
    </div>
);

export default userBlock;
