import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {

  state = {
    bookInfo: {
      title: '',
      author: '',
    }
  }

  buttonClickHandler = () => {
    axios.post('https://course-project-react.firebaseio.com/books',this.state.bookInfo).then(response => {
      console.log(response)
    })
  }

  render() {
    return (
      <div className = 'App'>

        <input type="text" name = 'title' placeholder = 'title' ref={c => {this.setState({bookInfo:{title:c}})}}/>
        <input type="text" name = 'author' placeholder = 'author' ref={c => {this.setState({bookInfo:{title:c}})}}/>

        <button onClick = {this.buttonClickHandler}>OK</button>
      </div>
    );
  }
}

export default App;
