import React, { Component } from 'react';
import axios from 'axios';
import Truck from './Truck/Truck';
import classes from './Trucks.module.css';

class Trucks extends Component {
    state = {
        truckList: null,
        loading: true,
        searchRequest: '',
    }

    shouldComponentUpdate(_, nextState) {
        return this.state.trucklist !== nextState.trucklist || this.state.loading !== nextState.loading;
    }

    componentDidMount() {
        axios
            .get('https://course-project-react.firebaseio.com/trucks.json')
            .then(response => {
                this.setState({truckList: response.data, loading: false});
            })
            .catch(error => {
                console.log(error.message);
                this.setState({loading: false});
            })
    }

    searchButtonHandler = () => {
        axios
            .post('', this.state.searchRequest)
            .then(response => {

            })
            .catch(error => {

            });
    }

    setSearchRequest = event => {
        this.setState({searchRequest: event.target.value});
    }

    render() {
        console.log('rendered');
        let trucks = <p>Loading...</p>

        if( !this.state.loading ) {
            trucks = this.state.truckList.map(truck => {     
                return (
                    <Truck
                        key = {truck.registrationPlate}
                        brand = {truck.brand}
                        model = {truck.model}
                        price = {truck.price}
                        years = {truck.amountYear}
                        country = {truck.country}
                        licensePlate = {truck.registrationPlate}
                        source = {truck.imagePath}
                    />
                );
            }); 
        }

        return(
            <div className = {classes.Trucks}>
                <input type="search" placeholder = "Search" onChange = {this.setSearchRequest}/>
                <button onClick = {this.searchButtonHandler}>Search</button>
                <div style = {{margin:"10px"}}>
                    <label htmlFor="sort">Sort by: </label>
                    <select id="sort" style = {{borderRadius: "10px", margin: "10px"}} onChange = {console.log('changed!')}>
                        <option value="price">Price</option>
                        <option value="year">Years</option>
                        <option value="brand">Brand</option>
                    </select>
                    <button style = {{height: '30px', width: '60px', borderRadius: '10px'}} onClick = {() => {}}>Sort</button>
                </div>
                {this.state.loading ? <p>Loading...</p> : trucks}
            </div>
        );
    }
}

export default Trucks;
