import React, { Component } from "react";
import classes from "./Truck.module.css";
import { Row, Col } from "react-bootstrap";

class Truck extends Component {

    state = {
        currency: 'USD'
    }

    formatPriceString = price => {
        let priceStringfied = price.toString().split('');
        for (let i = priceStringfied.length - 3; i > 0; i -= 3) {
            priceStringfied.splice(i,0," ");
        }
        return priceStringfied.join('');
    }

    changeCurrency = newCurrency => {

    }

    render() {
        return (
            <div className={classes.Truck}>
                <Row>
                    <Col md={6}>
                        <img src={this.props.source} alt="truck" />
                    </Col>
                    <Col md={6}>
                        <h1>
                            {this.props.brand} {this.props.model}
                        </h1>
                        <ul>
                            <li>
                                Price: <strong>{this.formatPriceString(this.props.price)}</strong>
                                <select id="currency" onChange = {() => console.log('changed!')}>
                                    <option value="USD">$</option>
                                    <option value="EUR">€</option>
                                    <option value="UAH">₴</option>
                                </select>
                            </li>
                            <li>
                                License plate: <strong>{this.props.licensePlate}</strong>
                            </li>
                            <li>
                                Country: <strong>{this.props.country}</strong>
                            </li>
                            <li>
                                Age (years): <strong>{this.props.years}</strong>
                            </li>
                        </ul>
                        { 
                            this.props.admin ?
                                <div style = {{position: 'absolute', right: '15px', bottom: '0'}}>
                                    <button onClick={() => {}} style = {{width: '50px', borderRadius: '10px'}}>Edit</button>
                                </div>
                            : null
                        }
                        
                    </Col>
                </Row>
            </div>
        );
    }
};

export default Truck;
