import React, { Component } from "react";
import classes from "../LoginForm/LoginForm.module.css";
import axios from "axios";

class SignUpForm extends Component {
    state = {
        email: "",
        password: "",
        passwordConf: "",
        firstName: "",
        secondName: "",
        sex: "",
        day: '1',
        month: '1',
        year: '1950',
        imagePath: "",

        emailError: "",
        passwordError: "",
        firstNameError: "",
        secondNameError: "",
    };

    validate = () => {
        this.setState({ emailError: "", passwordError: "" });
        let check = true;

        if (
            !/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
                this.state.email
            )
        ) {
            this.setState({ emailError: "wrong email" });
            check = false;
        }

        if (this.state.password !== this.state.passwordConf) {
            this.setState({ passwordError: "password is not confirmed" });
            check = false;
        }

        if (this.state.password.length < 5) {
            this.setState({ passwordError: "password is too short" });
            check = false;
        }

        if (/[a-zA-z]/.test(this.state.firstName)) {
            this.setState({ firstNameError: "error" });
            check = false;
        }

        if (/[a-zA-z]/.test(this.state.secondName)) {
            this.setState({ secondNameError: "error" });
            check = false;
        }

        return check;
    };

    shouldComponentUpdate(nextProps, nextState) {
        return (
            nextProps.visible !== this.props.visible ||
            nextState.status !== this.state.status ||
            nextState.passwordError !== this.state.passwordError ||
            nextState.emailError !== this.state.emailError
        );
    }

    setValue = (value, key) => {
        const newState = { ...this.state };
        newState[key] = value;
        this.setState(newState);
    };

    componentDidUpdate(_, prevState) {
        if (prevState === this.state) {
            this.setState({ emailError: "", passwordError: "" });
        }
    }

    okButtonHandler = () => {
        let valid = this.validate();
        if (valid) {
            console.log(this.state);
            axios
                .post("http://localhost:8088/api/user/login", {
                    email: this.state.email,
                    password: this.state.password,
                    dateOfBirth: `${this.state.day}.${this.state.month}.${this.state.day.year}`,
                    sex: this.state.sex,
                    imagePath: this.state.imagePath
                })
                .then(response => {
                    this.props.close();
                })
                .catch(error => {
                    this.props.close();
                });
        }
    };

    render() {
        const days = [];
        const years = [];
        const months = [];
        for(let i = 1; i <= 31; ++i) {
            days.push(i);
        }
        for(let i = 1; i <= 12; ++i) {
            months.push(i);
        }
        for(let i = 1950; i <= 2020; ++i) {
            years.push(i);
        }

        const yearsOpt = years.map(year => <option key = {year} value = {year}>{year}</option>);
        const monthsOpt = months.map(month => <option key = {month} value = {month}>{month}</option>);
        const daysOpt = days.map(day => <option key = {day} value = {day}>{day}</option>);

        return (
            <div
                className={classes.LoginForm}
                style={{
                    top:'60px',
                    opacity: this.props.visible ? 1 : 0,
                    transform: this.props.visible
                        ? "translateY(0)"
                        : "translateY(-100vh)",
                }}
            >
                <h3>Sign Up:</h3>
                <input
                    type="text"
                    placeholder="first name"
                    onChange={event =>
                        this.setValue(event.target.value, "firstName")
                    }
                />

                <input
                    type="text"
                    placeholder="second name"
                    onChange={event =>
                        this.setValue(event.target.value, "secondName")
                    }
                />
                <input
                    type="login"
                    placeholder="e-mail"
                    onChange={event =>
                        this.setValue(event.target.value, "email")
                    }
                />
                <select name="sex" onChange = {event => this.setValue(event.target.value, 'sex')}>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                </select>
                <div>
                <select name="day" onChange = {event => this.setValue(event.target.value, 'day')}>{daysOpt}</select>
                <select name="month" onChange = {event => this.setValue(event.target.value, 'month')}>{monthsOpt}</select>
                <select name="year" onChange = {event => this.setValue(event.target.value, 'year')}>{yearsOpt}</select>
                </div>
                <input
                    type="password"
                    placeholder="password"
                    onChange={event =>
                        this.setValue(event.target.value, "password")
                    }
                />

                <input
                    type="password"
                    placeholder="confirm password"
                    onChange={event =>
                        this.setValue(event.target.value, "passwordConf")
                    }
                />

                <input
                    type="imageAdress"
                    placeholder="image adress"
                    onChange={event =>
                        this.setValue(event.target.value, "imagePath")
                    }
                />
                <div style={{ color: "red" }}>
                    <small>{this.state.emailError}</small>
                    <br />
                    <small>{this.state.passwordError}</small>
                </div>
                <button
                    style={{ width: "80px" }}
                    onClick={this.okButtonHandler}
                >
                    OK
                </button>
                <button style={{ width: "80px" }} onClick={this.props.close}>
                    Cancel
                </button>
            </div>
        );
    }
}

export default SignUpForm;
