import React, { Component } from "react";
import classes from "../LoginForm/LoginForm.module.css";
import axios from "axios";

class SignUpForm extends Component {
    state = {
        email: "",
        password: "",
        passwordConf: "",

        emailError: "",
        passwordError: "",
    };

    validate = () => {
        this.setState({emailError: "", passwordError:""});
        let check = true;

        if(!/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(this.state.email)) {
            this.setState({emailError: "wrong email"})
            check = false;
        }

        if(this.state.password.length < 5) {
            this.setState({passwordError: "password is too short"})
            check = false;
        }

        if(this.state.password !== this.state.passwordConf) {
            this.setState({passwordError: "password is not confirmed"})
            check = false;
        }

        return check;
    };

    shouldComponentUpdate(nextProps, nextState) {
        return (
            nextProps.visible !== this.props.visible ||
            nextState.status !== this.state.status ||
            nextState.passwordError !== this.state.passwordError ||
            nextState.emailError !== this.state.emailError
        );
    }

    setValue = (value, key) => {
        const newState = { ...this.state };
        newState[key] = value;
        this.setState(newState);
    };

    okButtonHandler = () => {
        let valid = this.validate();
        if (valid) {
            console.log(this.state);
            axios
                .post("http://localhost:8088/api/user/login", this.state)
                .then(response => {
                    this.props.setUserInfo(response.data);
                    console.log(response.data);
                })
                .catch(error => {
                    console.log(error);
                });
        }
    };

    render() {
        return (
            <div
                className={classes.LoginForm}
                style={{
                    opacity: this.props.visible ? 1 : 0,
                    transform: this.props.visible
                        ? "translateY(0)"
                        : "translateY(-100vh)",
                }}
            >
                <h3>Sign Up:</h3>

                <input
                    type="email"
                    placeholder="e-mail"
                    onChange={event =>
                        this.setValue(event.target.value, "email")
                    }
                />
                <div style={{ color: "red" }}>
                    <small>{this.state.emailError}</small>
                </div>
                <input
                    type="password"
                    placeholder="password"
                    onChange={event =>
                        this.setValue(event.target.value, "password")
                    }
                />

                <input
                    type="password"
                    placeholder="confirm password"
                    onChange={event =>
                        this.setValue(event.target.value, "passwordConf")
                    }
                />
                <div style={{ color: "red" }}>
                    <small>{this.state.passwordError}</small>
                </div>
                <button
                    style={{ width: "80px" }}
                    onClick={this.okButtonHandler}
                >
                    OK
                </button>
            </div>
        );
    }
}

export default SignUpForm;
