import React from "react";
import classes from "./Truck.module.css";

const truck = props => {
    return (
        <div className={classes.Truck}>
            <div className={classes.ImageHolder}>
                <img src={props.source} alt="truck"/>
            </div>
            <div className = {classes.TruckInfo}>
            <h3>{props.brand} {props.model}</h3>
                <p>Price: <strong>${props.price}</strong></p>
                <p>License plate: <strong>{props.licensePlate}</strong></p>
                <p>Country: <strong>{props.country}</strong></p>
                <p>Age (years): <strong>{props.years}</strong></p>
                {props.type === 'admin' ? <button onClick={() => {}}>Edit</button> : null}
            </div>
        </div>
    );
};

export default truck;
