import React from "react";
import classes from "./UserItem.module.css";
import { Row, Col } from "react-bootstrap";
import Truck from "../../../Trucks/Truck/Truck";
import { Component } from "react";
import axios from 'axios';

class UserItem extends Component {
    state = {
        showTrucks: false,
        status: true//this.props.isActive,
    }

    trucksToggle = () => {
        this.setState({showTrucks: !this.state.showTrucks});
    }

    activeBannedToggle = () => {
        axios
            .post('llll/userId', {isActive: !this.state.status});
            this.setState({status: !this.state.status});
    }

    render () {
        const trucks = this.props.trucks.map(truck => (
            <Truck
                key={truck.id}
                truckId={truck.id}
                brand={truck.brand}
                model={truck.model}
                priceUSD={truck.priceUSD}
                priceEUR={truck.priceEUR}
                priceUAH={truck.priceUAH}
                yearGraduation={truck.yearGraduation}
                years={truck.amountYear}
                country={truck.country}
                licensePlate={truck.registrationPlate}
                source={truck.imagePath}
                admin={false}
                preview
            />
        ));

        let status;
        if(this.state.status) {
            status = <div onClick = {this.activeBannedToggle()} style = {{color: 'green'}}><p>active</p></div>;
        } else {
            status = <div onClick = {this.activeBannedToggle()} style = {{color: 'red'}}><p>banned</p></div>;
        }
            
        return (
            <div className={classes.UserItem} onClick = {this.trucksToggle}>
                <Row>
                    <Col md={6} style = {{textAlign:'center'}}>
                        <img src={this.props.source} width = "100%" height='100%' alt="user" />
                    </Col>
                    <Col md={6}>
                        <ul>
                            <li>Name: {this.props.name}</li>
                            <li>Age: {this.props.age}</li>
                            <li>Country: {this.props.country}</li>
                            <li>{status}</li>
                        </ul>
                    </Col>
                </Row>
                <Row>
                    {this.state.showTrucks ? trucks : null}
                </Row>
            </div>
        );
    }
    
};

export default UserItem;
