import React, { Component } from "react";
import "./App.css";
import axios from "axios";

class App extends Component {
    state = {
        bookInfo: {
            title: "",
            author: "",
        },
        bookShelf: null,
        loading: false,
    };

    shouldComponentUpdate(_, nextState) {
        return (
            this.state.bookShelf !== nextState.bookShelf ||
            this.state.loading !== nextState.loading
        );
    }

    okButtonClickHandler = () => {
        const config = {
            headers: {
                "Access-Control-Allow-Origin": "*",
            },
        };

        axios
            .post(
                "https://course-project-react.firebaseio.com/books.json",
                this.state.bookInfo,
                config
            )
            .then(response => {
                console.log(response);
            });
    };

    loadButtonClickHandler = () => {
        const config = {
            headers: {
                "Access-Control-Allow-Origin": "*",
            },
        };
        this.setState({ loading: true });
        axios
            .get(
                "https://course-project-react.firebaseio.com/bookShelf.json",
                config
            )
            .then(response => {
                this.setState({ bookShelf: response.data, loading: false });
                console.log(response);
            });
    };

    setTitle = event => {
        const newState = { ...this.state };
        newState.bookInfo.title = event.target.value;
        this.setState(newState);
    };

    setAuthor = event => {
        const newState = { ...this.state };
        newState.bookInfo.author = event.target.value;
        this.setState(newState);
    };

    render() {
        console.log("rendered");

        let bookList = null;

        if (this.state.loading) {
            bookList = <p>Loading...</p>;
        } else {
            if (this.state.bookShelf) {
                bookList = this.state.bookShelf.map(book => {
                    return (
                        <div key={book.id} className="Book">
                            <img src={book.image}  alt="car" height = "200px" width = "200px"/>
                            <h4>{book.title}</h4>
                            <p>{book.author}</p>
                        </div>
                    );
                });
            }
        }

        return (
            <div className="App">
                <div className="Container">
                    <h3>POST</h3>

                    <input
                        type="text"
                        name="title"
                        placeholder="Title"
                        onChange={this.setTitle}
                    />
                    <input
                        type="text"
                        name="author"
                        placeholder="Author"
                        onChange={this.setAuthor}
                    />

                    <button onClick={this.okButtonClickHandler}>OK</button>
                </div>
                <div className="Container">
                    <h3>GET</h3>
                    <button onClick={this.loadButtonClickHandler}>Load</button>
                    {bookList}
                </div>
            </div>
        );
    }
}

export default App;
