import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {

  state = {
    bookInfo: {
      title: '',
      author: '',
    }
  }

  shouldComponentUpdate() {
    return false;
  }

  buttonClickHandler = () => {
    axios.post('https://course-project-react.firebaseio.com/books.json',this.state.bookInfo).then(response => {
      console.log(response)
    })
  }

  setTitle = event => {
    this.setState({
      bookInfo: {
        title: event.target.value
      }
    })
  }

  setAuthor = event => {
    this.setState({
      bookInfo: {
        author: event.target.value
      }
    })
  }

  render() {
    return (
      <div className = 'App'>

        <input type="text" name = 'title' placeholder = 'Title' onChange = {this.setTitle}/>
        <input type="text" name = 'author' placeholder = 'Author' onChange = {this.setAuthor}/>

        <button onClick = {this.buttonClickHandler}>OK</button>
      </div>
    );
  }
}

export default App;
