import React from "react";
import classes from "./Truck.module.css";
import { Row, Col } from "react-bootstrap";

const truck = props => {

    const formatPriceString = price => {
        let priceStringfied = price.toString().split('');
        for (let i = priceStringfied.length; i > 0; i -= 1) {
            priceStringfied.splice(i,0,"_");
        }
        return priceStringfied.join('');
    }

    return (
        <div className={classes.Truck}>
            <Row>
                <Col md={6}>
                    <img src={props.source} alt="truck" />
                </Col>
                <Col md={6}>
                    <h1>
                        {props.brand} {props.model}
                    </h1>
                    <ul>
                        <li>
                            Price: <strong>{formatPriceString(props.price)}</strong>
                            <select id="currency" onChange = {() => console.log('changed!')}>
                                <option value="USD">$</option>
                                <option value="EUR">€</option>
                                <option value="UAH">₴</option>
                            </select>
                        </li>
                        <li>
                            License plate: <strong>{props.licensePlate}</strong>
                        </li>
                        <li>
                            Country: <strong>{props.country}</strong>
                        </li>
                        <li>
                            Age (years): <strong>{props.years}</strong>
                        </li>
                    </ul>
                    { 
                        props.admin ?
                            <div style = {{position: 'absolute', right: '15px', bottom: '0'}}>
                                <button onClick={() => {}} style = {{width: '50px', borderRadius: '10px'}}>Edit</button>
                            </div>
                        : null
                    }
                    
                </Col>
            </Row>
        </div>
    );
};

export default truck;
