import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {

  state = {
    title: '',
    author: '',
  }

  shouldComponentUpdate() {
    return false;
  }

  buttonClickHandler = () => {
    axios.post('https://course-project-react.firebaseio.com/books.json',this.state).then(response => {
      console.log(response)
    })
  }

  setTitle = event => {
    this.setState({
      title: event.target.value
    })
  }

  setAuthor = event => {
    this.setState({
      author: event.target.value
    })
  }

  render() {
    console.log('rendered');
    return (
      <div className = 'App'>

        <input type="text" name = 'title' placeholder = 'title' onChange = {this.setTitle}/>
        <input type="text" name = 'author' placeholder = 'author' onChange = {this.setAuthor}/>

        <button onClick = {this.buttonClickHandler}>OK</button>
      </div>
    );
  }
}

export default App;
