import React, { Component } from 'react';
import classes from './TruckInfo.module.css';
import Backdrop from '../../../../components/UI/Backdrop/Backdrop';
import axios from 'axios';

class TruckInfo extends Component {

    state = {
        price: this.props.price,
        country: this.props.country,
        licensePlate: this.props.licensePlate,
        years: this.props.years,
        brand: this.props.brand,
        modal: this.props.modal,
    }

    shouldComponentUpdate(nextProps) {
        return nextProps.visible !== this.props.visible;
    }

    updateTruck = () => {
        axios
            .put('.../this.props.truckId', this.state)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })

    } 
    
    render() {
        return(
            <React.Fragment>
                <div className = {classes.TruckInfo}
                    style = {{
                        opacity: this.props.visible ? 1 : 0,
                        transform: this.props.visible ? 'translateY(1)' : 'translateY(-100vh)'
                    }}>
                    <h3>{this.props.brand} {this.props.model}</h3>
                    <ul>
                        <li>Price ($): <input type="text" defaultValue = {this.props.price}/></li>
                        <li>License Plate: <input type="text" defaultValue = {this.props.licensePlate}/></li>
                        <li>Country: <input type="text" defaultValue = {this.props.country}/></li>
                        <li>Age (years): <input type="text" defaultValue = {this.props.years}/></li>
                    </ul>
                    <button style = {{borderRadius: '10px'}} onClick = {this.props.enableRegularMode}>Cancel</button>
                    <button style = {{borderRadius: '10px'}} onClick = {() => {}}>Apply Changes</button>
                </div>
                <Backdrop show = {this.props.visible} regularMode = {this.props.enableRegularMode}/>  
            </React.Fragment>
        );
    }
    
}

export default TruckInfo;
