import React, { Component } from "react";
import classes from "./LoginForm.module.css";
import axios from "axios";

class LoginForm extends Component {
    state = {
        login: "",
        password: ""
    };

    shouldComponentUpdate(nextProps, nextState) {
        return nextProps.visible !== this.props.visible || nextState.status !== this.state.status;
    }

    setValue = (value, key) => {
        const newState = { ...this.state };
        newState[key] = value;
        this.setState(newState);
    };

    okButtonHandler = () => {
        console.log(this.state);
        axios
            .post('http://localhost:8088/api/user', this.state)
            .then(response => {
                this.props.setStatus(this.response.data)
            })
            .catch(error => {
                console.log(error);
            });
    };

    render() {
        return (
            <div
                className={classes.LoginForm}
                style={{
                    opacity: this.props.visible ? 1 : 0,
                    transform: this.props.visible
                        ? "translateY(0)"
                        : "translateY(-100vh)",
                }}
            >
                <h3>Sign in:</h3>
                <input
                    type="email"
                    placeholder="e-mail"
                    onChange={event =>
                        this.setValue(event.target.value, "login")
                    }
                />
                <input
                    type="password"
                    placeholder="password"
                    onChange={event =>
                        this.setValue(event.target.value, "password")
                    }
                />
                <button
                    style={{ width: "80px" }}
                    onClick={this.okButtonHandler}
                >
                    OK
                </button>
            </div>
        );
    }
}

export default LoginForm;
