import React, { Component } from 'react';
import classes from './TruckInfo.module.css';
import Backdrop from '../../../../components/UI/Backdrop/Backdrop';

class TruckInfo extends Component {

    shouldComponentUpdate(nextProps) {
        return nextProps.visible !== this.props.visible || nextProps.showSpinner !== this.props.showSpinner;
    }
    
    render() {
        return(
            <React.Fragment>
                <div className = {classes.TruckInfo}
                    style = {{
                        opacity: this.props.visible ? 1 : 0,
                        transform: this.props.visible ? 'translateY(0)' : 'translateY(-100vh)'
                    }}>
                    <h3>{this.props.brand} {this.props.model}</h3>
                    <ul>
                        <li><p>Price: $<strong>{this.props.price}</strong></p></li>
                        <li>License Plate: <p>{this.props.licensePlate}</p></li>
                        <li>Country<p>{this.props.country}</p></li>
                        <li>Age(years): <p>{this.props.years}</p></li>
                    </ul>
                </div>
                <Backdrop show = {this.props.visible} regularMode = {this.props.enableRegularMode}/>  
            </React.Fragment>
        );
    }
    
}

export default TruckInfo;
