import React, { Component } from 'react';
import axios from 'axios';
import Truck from './Truck/Truck';

class Trucks extends Component {
    state = {
        truckList: null,
        loading: true,
    }

    componentDidMount() {
        axios
            .get('https://course-project-react.firebaseio.com/trucks.json')
            .then(response => {
                this.setState({truckList: response.data, loading: false});
                console.log('1');
            })
            .catch(error => {
                console.log(error.message);
                this.setState({loading: false});
            })
    }

    render() {
        let trucks = this.state.truckList.map(truck => {     
            return (
                <Truck
                    key = {truck.registrationPlate}
                    brand = {truck.brand}
                    model = {truck.model}
                    price = {truck.price}
                    years = {truck.amountYear}
                    country = {truck.country}
                    licensePlate = {truck.registrationPlate}
                    source = {truck.imagePath}
                />
            );
        });

        if( this.state.loading ) {
            trucks = <p>Loading...</p>
        }

        return(
            <div className = 'Trucks'>
                {this.state.loading ? <p>Loading...</p> : trucks}
            </div>
        );
    }
}

export default Trucks;
